import React from 'react';
import Chat from 'react-simple-chatbot';
import { Segment } from 'semantic-ui-react';
import { Redirect } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
    };
  }

  handleEnd({ steps }) {
    const { Age, Name } = steps;
    console.log(`Name: ${Name.value}`);
    console.log(`Age: ${Age.value}`);

    setTimeout(() => {
      this.setState({ redirect: true });
    }, 5000);
  }

  render() {
    const ageOptions = [];
    for (let i = 18; i <= 40; i++) {
      ageOptions.push({ value: i, label: i.toString(), trigger: 'ThankYou' });
    }

    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        delay: 3000,
        trigger: 'GotIt',
      },
      {
        id: 'GotIt',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        options: ageOptions,
      },
      {
        id: 'ThankYou',
        message: 'Thank you. In 5 seconds, bot will exit.',
        end: false,
      },
      {
        id: 'Countdown',
        component: <RedirectToExit />,
        waitAction: true,
        asMessage: true,
      },
    ];

    const RedirectToExit = () => {
      if (this.state.redirect) {
        return <Redirect to="/exit" />;
      }
      return null;
    };

    return (
      <Segment floated="center">
        <Chat steps={steps} handleEnd={this.handleEnd.bind(this)} />
      </Segment>
    );
  }
}

export default App;
