import React from 'react';
import Chat from 'react-simple-chatbot';
import { Segment } from 'semantic-ui-react';
import { useNavigate } from 'react-router-dom';
import RedirectToExit from './RedirectToExit';

class App extends React.Component {
    handleEnd = ({ steps, triggerNextStep }) => {
      const { Age, Name } = steps;
      console.log(`Name: ${Name.value}`);
      console.log(`Age: ${Age.value}`);
      triggerNextStep({ value: 'Exit', trigger: 'Exit' });
    };
  
    render() {
        const ageOptions = [];
        for (let i = 18; i <= 40; i++) {
          ageOptions.push({ value: i, label: i.toString(), trigger: 'ThankYou' });
        }

    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        delay: 3000,
        trigger: 'GotIt',
      },
      {
        id: 'GotIt',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        options: ageOptions,
      },
      {
        id: 'ThankYou',
        message: 'Thank you. In 5 seconds, bot will exit.',
        end: false,
      },
      {
        id: 'Exit',
        component: <RedirectToExit />,
        asMessage: false,
        waitAction: true,
        metadata: {
          name: '{{Name.value}}',
          age: '{{Age.value}}',
        },
      },
    ];

    return (
        <Segment style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: 'linear-gradient(to bottom, #f0f0f0, #e1e1e1)' }}>
          <Chat steps={steps} handleEnd={this.handleEnd} />
        </Segment>
      );  
  }
}

export default App;
