import React, { useState, useEffect } from 'react';

const Chatbot = () => {
  const [name, setName] = useState('');
  const [age, setAge] = useState('');
  const [messageIndex, setMessageIndex] = useState(0);
  const messages = [
    'Hello, Welcome to the student info system!',
    'Enter your Name',
    'Enter your Age',
    'Thank you. In 5 seconds, the bot will exit',
  ];

  useEffect(() => {
    if (messageIndex === messages.length - 1) {
      // Perform any necessary actions before redirecting to Page 3
      setTimeout(() => {
        // Redirect to Page 3
      }, 5000);
    }
  }, [messageIndex]);

  const handleButtonClick = () => {
    setMessageIndex((prevIndex) => prevIndex + 1);
  };

  const handleMessageChange = (e) => {
    const { value } = e.target;
    setName(value);
  };

  const handleAgeChange = (e) => {
    const { value } = e.target;
    setAge(value);
  };

  return (
    <div className="chatbot">
      <div className="chatbot-messages">
        {messageIndex < messages.length && (
          <div className="message">{messages[messageIndex]}</div>
        )}
      </div>
      {messageIndex === 1 && (
        <div className="input-field">
          <input type="text" value={name} onChange={handleMessageChange} />
        </div>
      )}
      {messageIndex === 2 && (
        <div className="input-field">
          <select value={age} onChange={handleAgeChange}>
            <option value="">Select Age</option>
            <option value="18">18</option>
            <option value="19">19</option>
            {/* Add more options for age range */}
            <option value="40">40</option>
          </select>
        </div>
      )}
      {messageIndex !== messages.length - 1 && (
        <button onClick={handleButtonClick}>Got it!</button>
      )}
    </div>
  );
};

export default Chatbot;
