import React from 'react';
import Chat from 'react-simple-chatbot';
import './App.css';
import { Segment } from 'semantic-ui-react';

class App extends React.Component {
  handleEnd({ steps }) {
    const lastStep = steps[steps.length - 1];
    const { value } = lastStep;

    console.log(`Name: ${steps[2].value}`);
    console.log(`Age: ${value}`);
  }

  render() {
    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        trigger: 'Got it',
      },
      {
        id: 'Got it',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter your Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        user: true,
        end: true,
      },
    ];

    return (
      <Segment floated="center">
        <Chat steps={steps} handleEnd={this.handleEnd} />
      </Segment>
    );
  }
}

export default App;
