import React from 'react';
import Chat from 'react-simple-chatbot';
import './App.css';
import { Segment } from 'semantic-ui-react';

class App extends React.Component {
  handleEnd({ steps, triggerNextStep }) {
    const lastStep = steps[steps.length - 1];
    const age = lastStep.value;

    console.log(`Name: ${steps[2].value}`);
    console.log(`Age: ${age}`);

    setTimeout(() => {
      triggerNextStep({ value: 'ThankYou', trigger: 'ThankYou' });
    }, 5000);
  }

  render() {
    const ageOptions = [];
    for (let i = 18; i <= 40; i++) {
      ageOptions.push({ value: i.toString(), label: i.toString(), trigger: "UserAge" });
    }

    // const steps=[
    //   {
    //     id: 'hello-world',
    //     message: 'Hello World!',
    //     trigger: "hello-yo"
    //   },
    //   {
    //     id: 'hello-yo',
    //     message: 'Yo!',
    //   },
    // ]

    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        delay: 3000,
        trigger: 'GotIt',
      },
      {
        id: 'GotIt',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        options: ageOptions,
        trigger: 'UserAge',
      },
      {
        id: 'UserAge',
        message: 'Thank Y',
        user: true,
        validator: (value) => {
          return true;
        },
        trigger: 'ThankYou',
      },
      {
        id: 'ThankYou',
        message: 'Thank you. In 5 seconds, bot will exit.',
        end: true,
      },
    ];

    return (
      <Segment floated="center">
        <Chat steps={steps} handleEnd={this.handleEnd} />
      </Segment>
    );
  }
}

export default App;
