import React from 'react';
import Chat from 'react-simple-chatbot';
import { Segment } from 'semantic-ui-react';
import { withRouter } from 'react-router-dom';

class App extends React.Component {
  render() {
    const ageOptions = [];
    for (let i = 18; i <= 40; i++) {
      ageOptions.push({ value: i, label: i.toString(), trigger: 'ThankYou' });
    }

    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        delay: 3000,
        trigger: 'GotIt',
      },
      {
        id: 'GotIt',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        options: ageOptions,
      },
      {
        id: 'ThankYou',
        message: 'Thank you. In 5 seconds, bot will exit.',
        end: false,
      },
      {
        id: 'Countdown',
        component: withRouter(RedirectToExit),
        waitAction: true,
        asMessage: true,
      },
    ];

    const RedirectToExit = withRouter(({ history }) => {
      React.useEffect(() => {
        const timer = setTimeout(() => {
          history.push('/exit');
        }, 5000);

        return () => {
          clearTimeout(timer);
        };
      }, [history]);

      return null;
    });

    return (
      <Segment floated="center">
        <Chat steps={steps} customComponents={{ RedirectToExit }} />
      </Segment>
    );
  }
}

export default App;
