import React from 'react';
import Chat from 'react-simple-chatbot';
import './App.css';
import { Segment } from 'semantic-ui-react';

class App extends React.Component {
  handleEnd({ steps }) {
    const lastStep = steps[steps.length - 1];
    const { value } = lastStep;

    if (value === 'React') {
      // Handle React issue
      console.log('React issue');
    } else if (value === 'Angular') {
      // Handle Angular issue
      console.log('Angular issue');
    }
  }

  render() {
    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        trigger: 'Got it',
      },
      {
        id: 'Got it',
        options: [
          {
            value: 'Got it',
            label: 'Got it',
            trigger: 'AskName',
          },
        ],
      },
      {
        id: 'AskName',
        message: 'Enter your Name',
        trigger: 'Age',
      },
      {
        id: 'Age',

      },
      {
        id: 'End',
        message: 'Thanks for letting us know. Our team will resolve your issue ASAP',
        end: true,
      },
    ];

    return (
      <Segment floated="center">
        <Chat steps={steps} handleEnd={this.handleEnd} />
      </Segment>
    );
  }
}

export default App;
