import React from 'react';
import Chat from 'react-simple-chatbot';
import './App.css';
import { Segment } from 'semantic-ui-react';

class App extends React.Component {
  handleEnd({ steps }) {
    const lastStep = steps[steps.length - 1];
    const age = lastStep.value;

    console.log(`Name: ${steps[2].value}`);
    console.log(`Age: ${age}`);

    setTimeout(() => {
      console.log('Bot will exit');
    }, 5000);
  }

  render() {
    const ageOptions = [];
    for (let i = 18; i <= 40; i++) {
      ageOptions.push({ value: i.toString(), label: i.toString() });
    }

    const steps = [
      {
        id: 'Greet',
        message: 'Hello, Welcome to the student info system!',
        delay: 3000,
        trigger: 'GotIt',
      },
      {
        id: 'GotIt',
        component: (
          <div>
            <p>Got it!</p>
            <p>Enter your Name</p>
          </div>
        ),
        asMessage: true,
        trigger: 'Name',
      },
      {
        id: 'Name',
        user: true,
        trigger: 'AskAge',
      },
      {
        id: 'AskAge',
        message: 'Enter your Age',
        trigger: 'Age',
      },
      {
        id: 'Age',
        options: ageOptions,
        trigger: 'UserAge',
      },
      {
        id: 'UserAge',
        user: true,
        trigger: 'ThankYou',
      },
      {
        id: 'ThankYou',
        message: 'Thank you. In 5 seconds, bot will exit.',
        end: true,
      },
    ];

    return (
      <Segment floated="center">
        <Chat steps={steps} handleEnd={this.handleEnd} />
      </Segment>
    );
  }
}

export default App;
